<!--Modal Code-->
<script>
// Get the modal
var modal = document.getElementById('myModal');

// Get the button that opens the modal
var btn = document.getElementById("input_2");

// Get the <span> element that closes the modal
var span = document.getElementsByClassName("close")[0];

// When the user clicks the button, open the modal 

btn.onclick = function() {
    
  var e=document.getElementById("input_7");
  var value = e.options[e.selectedIndex].value.toString();
     
    if(!(value==="noteRedirect")){
     modal.style.display = "block";
    }else {
        var noteTitle=document.getElementById("input_10").value;
        document.location="CreateNote.php?title="+noteTitle;
    }
   }

// When the user clicks on <span> (x), close the modal
span.onclick = function() {
    modal.style.display = "none";
}

// When the user clicks anywhere outside of the modal, close it
window.onclick = function(event) {
    if (event.target == modal) {
        modal.style.display = "none";
    }
}

//Remove Spaces

function nospaces(t){

    if( t.value.match(/\s/g) || t.value.match(/\./g) || t.value.match(/\//g) || t.value.match(/\\/g)){

        alert('Username Cannot Have Spaces or Full Stops');

        t.value= (t.value.replace(/\s/g,'') .replace(/\./g,'').replace(/\//g,'').replace(/\\/g,''));

    }

}

</script>

<!--ShortURL availability check -->

<script type="text/javascript">
  


var $j = jQuery.noConflict();

$j(document).ready(function()
{    
	$j("#shortURL").keyup(function()
	{		
		var name = $j(this).val();	
		
		if(name.length > 3)
		{		
			$j("#shortUrlAvailability").html('checking...');
			
			/*$.post("username-check.php", $("#reg-form").serialize())
				.done(function(data){
				$("#result").html(data);
			});*/
			
			$j.ajax({
				
				type : 'POST',
				url  : 'Backend/ShortURL-Availability.php',
				data : $j(this).serialize(),
				success : function(data)
						  {
					         $j("#shortUrlAvailability").html(data);
					      }
				});
				return false;
			
		}
		else
		{
			$j("#shortUrlAvailability").html('');
		}
	});
	
});<!---->
</script>